import wikipedia

def lambda_handler(event, context):
    wikiSearchText=event['currentIntent']['slots']['searchText']
    try:
        textToLex=wikipedia.summary(wikiSearchText,sentences=1)
    except:
        textToLex = "Sorry, I dont have information about "+wikiSearchText+", please ask Jeevan or Vertex team"

    response = {
        'dialogAction': {
            'type': 'Close',
            'fulfillmentState': 'Fulfilled',
            'message': {
                'contentType': 'PlainText',
                'content': textToLex
                }
        }
    }

    return response